SELECT * FROM ( SELECT ROWNUM RNUM, SUPPLE_SEQ, SUPPLE_NAME, SUPPLE_RAW, SUPPLE_CAUTION, SUPPLE_FORMULA, SUPPLE_COMPANY, SUPPLE_FUNCTION, SUPPLE_DOSE, SUPPLE_REGDATE, SUPPLE_LINK, SUPPLE_STATUS, SUPPLE_TITLE, SUPPLE_EFFICACY FROM ( SELECT SUPPLE_SEQ, NVL(SUPPLE_NAME, '') SUPPLE_NAME, NVL(SUPPLE_RAW, '') SUPPLE_RAW, NVL(SUPPLE_CAUTION, '') SUPPLE_CAUTION, NVL(SUPPLE_FORMULA, '') SUPPLE_FORMULA, NVL(SUPPLE_COMPANY, '') SUPPLE_COMPANY, NVL(SUPPLE_FUNCTION, '') SUPPLE_FUNCTION, NVL(SUPPLE_DOSE, '') SUPPLE_DOSE, NVL(SUPPLE_REGDATE, '') SUPPLE_REGDATE, NVL(SUPPLE_LINK, '') SUPPLE_LINK, NVL(SUPPLE_STATUS, 'N') SUPPLE_STATUS, NVL(SUPPLE_TITLE, '') SUPPLE_TITLE, NVL(SUPPLE_EFFICACY, '') SUPPLE_EFFICACY FROM TD_SUPPLE WHERE SUPPLE_STATUS = 'Y'
AND (SUPPLE_NAME LIKE '%' || #{searchValue} || '%' OR SUPPLE_RAW LIKE '%' || #{searchValue} || '%' OR SUPPLE_CAUTION LIKE '%' || #{searchValue} || '%' OR SUPPLE_FORMULA LIKE '%' || #{searchValue} || '%' OR SUPPLE_COMPANY LIKE '%' || #{searchValue} || '%' OR SUPPLE_FUNCTION LIKE '%' || #{searchValue} || '%' OR SUPPLE_DOSE LIKE '%' || #{searchValue} || '%' OR SUPPLE_REGDATE LIKE '%' || #{searchValue} || '%' OR SUPPLE_LINK LIKE '%' || #{searchValue} || '%' OR SUPPLE_STATUS LIKE '%' || #{searchValue} || '%' OR SUPPLE_TITLE LIKE '%' || #{searchValue} || '%' OR SUPPLE_EFFICACY LIKE '%' || #{searchValue} || '%')
ORDER BY SUPPLE_SEQ DESC ) ) WHERE RNUM BETWEEN #{start} AND #{end}
